import osimport refrom os.path import join,splitdatafolder = join('..','lunadata')def segmentedLungs_folder(contain):#todo    return join(datafolder,contain+'_segmentedLungs')def input_folder(contain):    return join(datafolder,contain)def slices_folder(contain):    return join(datafolder,contain+'_slices')def contain_folder(path):    if os.path.isdir(path):        return os.path.basename(path)    else:        return os.path.basename(os.path.dirname(path))def correct_path(path):    if 'nt' in os.name:#windows        path =  path.replace('/','\\')    else:        path = path.replace('\\','/')    return pathdef mkdir(path):    if not os.path.exists(path):        os.mkdir(path)def mkdir_iter(path):    if os.path.exists(path):        return    path1=split(path)    mkdir_iter(path1[0])    mkdir(path)